hooks.installCommand:
# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit",
  "command": "./scripts/format.sh"
}

# Add to your Claude Code hooks config
{
  "event": "PermissionRequest",
  "command": "./scripts/check-permissions.sh"
}

# Add to your Claude Code hooks config
{
  "event": "PreToolUse",
  "command": "./scripts/log-command.sh"
}

# Add to your Claude Code hooks config
{
  "event": "UserPromptSubmit",
  "command": "./scripts/notify.sh"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit|Write",
  "command": "./scripts/check-conventions.sh"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit|Write",
  "command": "./scripts/run-tests.sh"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit|Write",
  "prompt": "Analyze the changes and suggest a conventional commit message"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit",
  "command": "./scripts/scan-deps.sh"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit",
  "command": "./scripts/profile.sh"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit",
  "prompt": "Generate/update documentation for the modified code"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit",
  "command": "./scripts/check-complexity.sh"
}

# Add to your Claude Code hooks config
{
  "event": "SessionEnd",
  "command": "./scripts/session-summary.sh"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit",
  "prompt": "Review this code for bugs, security issues, and improvements"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit|Write",
  "command": "./scripts/validate-env.sh"
}

# Add to your Claude Code hooks config
{
  "event": "PostToolUse",
  "matcher": "Edit|Write",
  "command": "./scripts/validate-build.sh"
}

hooks.example:
#!/bin/bash
# validate-build.sh
if [[ "$TOOL_INPUT" =~ \.(ts|tsx)$ ]]; then
  npm run build 2>&1 | head -20
fi
exit ${PIPESTATUS[0]}
